mapper.select:
select , (SELECT ISNULL(NAME,'') NAME FROM SYS_DICTIONARIES WHERE DICTIONARIES_ID = f.ClassificationOfUse) ClassificationOfUseName, (SELECT ISNULL(NAME,'') NAME FROM SYS_DICTIONARIES WHERE DICTIONARIES_ID = f.KnowledgeClassification) KnowledgeClassificationName from f where 1=1 AND FStatus != '归档'
and f.PARENT_ID = #{pd.MFOLDER_ID}
and ( f.REMARKS LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or f.NAME LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') )
and f.MASTER = #{pd.USERNAME}
and f.SHARE = 'yes'
[fhstart]order by f.CTIME desc[fhend]

mapper.update:
		update

		set 


	                FStatus = #{SHARE} 


	                SHARE = #{SHARE}


		where 
			MFOLDER_ID in

                 #{item}
