mapper.select:
select isnull(count(*),0) zs from 
 f 
where f.FNum = #{FNum} 

	and f.ProcessRoute_ID <> #{ProcessRoute_ID}

select isnull(count(*),0) zs from 
 f 
where f.FName = #{FName} 

	and f.ProcessRoute_ID <> #{ProcessRoute_ID}

select isnull(count(*),0) zs from 
 f 
where f.Cabinet_Type = #{Cabinet_Type} 

	and f.ProcessRoute_ID <> #{ProcessRoute_ID}

select s.name FCreatePerson, from f LEFT JOIN OA_STAFF s ON f.FCreatePersonID=s.staff_id where 1=1
and ( f.FNum LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FName LIKE '%'+ #{pd.KEYWORDS}+'%' )
and f.FNum = #{pd.FNum}
and f.FName = #{pd.FName}
and f.FStatus = #{pd.FStatus}
[fhstart] ORDER BY f.FCreateTime DESC [fhend]

select s.name FCreatePerson, from f LEFT JOIN OA_STAFF s ON f.FCreatePersonID=s.staff_id where 1=1
and ( f.FNum LIKE '%'+ #{KEYWORDS}+'%' or f.FName LIKE '%'+ #{KEYWORDS}+'%' )
and f.FNum = #{FNum}
and f.FName = #{FName}
and f.FStatus = #{FStatus}
and f.Cabinet_Type = #{Cabinet_Type}
ORDER BY f.FCreateTime DESC
