mapper.select:
select
from f where 1=1
and f.FNum = #{pd.FNum}
and f.FName like '%'+#{pd.FName}+'%'
and f.QIMethod = #{pd.QIMethod}

select

from 
 f 
where 1=1 

	and
		(
			f.FName LIKE '%'+ #{KEYWORDS}+'%'
			 or 
			f.FNum LIKE '%'+ #{KEYWORDS}+'%' 

		)


	and f.FNum = #{FNum}


	and f.FName = #{FName}


	and f.QIMethod = #{QIMethod}


	and f.QIType = #{QIType}
