mapper.select:
select ,f1.NAME FStaffName,f2.NAME FTypeName from f left join OA_STAFF f1 on f.FCreatePersonID=f1.STAFF_ID left join SYS_DICTIONARIES f2 on f.FType=f2.DICTIONARIES_ID where 1=1
and f.FState!='禁用'
and ( f.FNumber LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FStepName LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FShowName LIKE '%'+ #{pd.KEYWORDS}+'%' )
and ( f2.NAME = #{pd.KEYWORDS_FTYPE}
)
[fhstart] order by FCreateTime desc [fhend]
