mapper.select:
select ,f1.NAME FStaffName,f2.NAME FTypeName from f left join OA_STAFF f1 on f.FCreatePersonID=f1.STAFF_ID left join SYS_DICTIONARIES f2 on f.FType=f2.DICTIONARIES_ID where 1=1 and SopSchemeTemplate_ID=#{pd.SopSchemeTemplate_ID}
and ( f.FNumber LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FStepName LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FShowName LIKE '%'+ #{pd.KEYWORDS}+'%' )
and ( f2.NAME = #{pd.KEYWORDS_FTYPE}
)
[fhstart] order by cast(FSortTime as NUMERIC(24,2)) [fhend]

select
from f where 1=1
and ( f.SopSchemeTemplate_ID = #{SopSchemeTemplate_ID}
)

mapper.update:
update

set 
	FSortTime = #{FSortTime},
	FIsFirst = #{FIsFirst}
where 
	SopStep_ID = #{SopStep_ID}

mapper.select:
select count(*) FNUM from KM_SopStep 
where 1=1 and SopSchemeTemplate_ID=#{SopSchemeTemplate_ID} and FIsFirst='是'

and SopStep_ID != #{SopStep_ID}
