mapper.update:
update

set 
	MAT_AUXILIARYMX_CODE = #{MAT_AUXILIARYMX_CODE},
	MAT_AUXILIARYMX_NAME = #{MAT_AUXILIARYMX_NAME}
where 
	MAT_AUXILIARYMX_ID = #{MAT_AUXILIARYMX_ID}

mapper.select:
select isnull(count(*),0) zs from 
 f 
where f.MAT_AUXILIARYMX_CODE = #{MAT_AUXILIARYMX_CODE} 

	and f.MAT_AUXILIARYMX_ID <> #{MAT_AUXILIARYMX_ID}

select
from f where f.MAT_AUXILIARY_ID = #{pd.MAT_AUXILIARY_ID}
and ( f.MAT_AUXILIARYMX_CODE LIKE '%'+ #{pd.KEYWORDS}+'%' or f.MAT_AUXILIARYMX_NAME LIKE '%'+ #{pd.KEYWORDS}+'%' )
and f.MAT_AUXILIARYMX_CODE = #{pd.MAT_AUXILIARYMX_CODE}
and f.MAT_AUXILIARYMX_NAME = #{pd.MAT_AUXILIARYMX_NAME}
[fhstart] order by f.MAT_AUXILIARYMX_CODE [fhend]

select

from 
 f 
where
	f.MAT_AUXILIARY_ID = #{MAT_AUXILIARY_ID}

	and
		(
			f.MAT_AUXILIARYMX_CODE LIKE '%'+ #{KEYWORDS}+'%'
			 or 
			f.MAT_AUXILIARYMX_NAME LIKE '%'+ #{KEYWORDS}+'%' 
		)


	and f.MAT_AUXILIARYMX_CODE = #{MAT_AUXILIARYMX_CODE}


	and f.MAT_AUXILIARYMX_NAME = #{MAT_AUXILIARYMX_NAME}

select top 100 MAT_AUXILIARYMX_ID,MAT_AUXILIARYMX_NAME,MAT_AUXILIARYMX_CODE,isnull(MAT_AUXILIARYMX_NAME,'无名称')+'/'+isnull(MAT_AUXILIARYMX_CODE,'无编号') FSelectOption from f where 1=1 and MAT_AUXILIARY_ID=#{MAT_AUXILIARY_ID}
and ( MAT_AUXILIARYMX_NAME LIKE '%'+ #{KEYWORDS}+'%' or MAT_AUXILIARYMX_CODE LIKE '%'+ #{KEYWORDS}+'%' )
and f.MAT_AUXILIARY_ID = #{MAT_AUXILIARY_ID}
order by f.MAT_AUXILIARYMX_CODE

mapper.delete:
delete from 
 
where MAT_AUXILIARY_ID = #{MAT_AUXILIARY_ID} 
and MAT_AUXILIARYMX_CODE = #{MAT_AUXILIARYMX_CODE}

mapper.select:
select * from 
 
where MAT_AUXILIARY_ID = #{MAT_AUXILIARY_ID} 
and MAT_AUXILIARYMX_CODE = #{MAT_AUXILIARYMX_CODE}
