mapper.select:
select isnull(count(*),0) zs from 
 f 
where f.UNIT_INFO_ID = #{UNIT_INFO_ID} 
	and f.MAT_SPEC_QTY=#{MAT_SPEC_QTY} 
	and f.MAT_BASIC_ID=#{MAT_BASIC_ID}

	and f.MAT_SPEC_ID <> #{MAT_SPEC_ID}

select f2.NAME FCreatorName, f3.NAME LastModifiedByName, unit.FCODE MAT_AUXILIARY_UNIT_CODE,unit.UNIT_INFO_ID MAT_AUXILIARY_UNIT,unit.FName MAT_AUXILIARY_UNIT_NAME, m.MAT_NAME,m.MAT_CODE,unit1.UNIT_INFO_ID MAT_MAIN_UNIT,unit1.FName MAT_MAIN_UNIT_NAME,unit1.FCODE MAT_MAIN_UNIT_CODE,
from f LEFT JOIN MBASE_MAT_BASIC m ON f.MAT_BASIC_ID = m.MAT_BASIC_ID LEFT JOIN MBASE_UNIT_INFO unit ON unit.UNIT_INFO_ID = f.UNIT_INFO_ID LEFT JOIN MBASE_UNIT_INFO unit1 ON unit1.UNIT_INFO_ID = m.MAT_MAIN_UNIT left join OA_STAFF f2 on f.FCreatePersonID=f2.STAFF_ID left join OA_STAFF f3 on f.LastModifiedBy=f3.STAFF_ID where 1=1
and ( unit.FName LIKE '%'+ #{pd.KEYWORDS}+'%' or m.MAT_NAME LIKE '%'+ #{pd.KEYWORDS}+'%' )
and f.MAT_BASIC_ID = #{pd.MAT_BASIC_ID}
and f.UNIT_INFO_ID = #{pd.UNIT_INFO_ID}
[fhstart] ORDER BY f.FCreateTime DESC [fhend]

select f2.NAME FCreatorName, f3.NAME LastModifiedByName, unit.FCODE MAT_AUXILIARY_UNIT_CODE,unit.UNIT_INFO_ID MAT_AUXILIARY_UNIT,unit.FName MAT_AUXILIARY_UNIT_NAME, m.MAT_NAME,m.MAT_CODE,unit1.UNIT_INFO_ID MAT_MAIN_UNIT,unit1.FName MAT_MAIN_UNIT_NAME,unit1.FCODE MAT_MAIN_UNIT_CODE,
from f LEFT JOIN MBASE_MAT_BASIC m ON f.MAT_BASIC_ID = m.MAT_BASIC_ID LEFT JOIN MBASE_UNIT_INFO unit ON unit.UNIT_INFO_ID = f.UNIT_INFO_ID LEFT JOIN MBASE_UNIT_INFO unit1 ON unit1.UNIT_INFO_ID = m.MAT_MAIN_UNIT left join OA_STAFF f2 on f.FCreatePersonID=f2.STAFF_ID left join OA_STAFF f3 on f.LastModifiedBy=f3.STAFF_ID where 1=1
and ( unit.FName LIKE '%'+ #{KEYWORDS}+'%' or m.MAT_NAME LIKE '%'+ #{KEYWORDS}+'%' )
and f.MAT_BASIC_ID = #{MAT_BASIC_ID}
and f.UNIT_INFO_ID = #{UNIT_INFO_ID}
ORDER BY f.FCreateTime ASC
