mapper.select:
select
from f where EQM_OVERHAUL_ID=#{pd.EQM_OVERHAUL_ID}
and ( f.FTYPE LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FCOUPLE_CONTENT LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FCREATOR LIKE '%'+ #{pd.KEYWORDS}+'%' )

mapper.update:
update

set 
	FCOUPLE_CONTENT = #{FCOUPLE_CONTENT},
	FEXTEND1 = #{FEXTEND1}
where 
	EQM_OVERHAULMX_ID = #{EQM_OVERHAULMX_ID}
