mapper.select:
select

from 
 f
where 1=1

	and
		(
			f.FNAME LIKE '%'+ #{pd.KEYWORDS_Eqm}+'%'
			 or 
			f.FNUMBER LIKE '%'+ #{pd.KEYWORDS_Eqm}+'%' 
			 or 
			f.FTYPE LIKE '%'+ #{pd.KEYWORDS_Eqm}+'%' 
			 or 
			f.FSPECS LIKE '%'+ #{pd.KEYWORDS_Eqm}+'%' 
		)


	and
		(
			f.FOPERATOR = #{pd.KEYWORDS_Man}
		)


	and
		(
			f.STATUS  =#{pd.KEYWORDS_STATUS}
		)


	and
		(
			f.CREATE_TIME  >= #{pd.lastStart}
		)


	and
		(
			f.CREATE_TIME  <= #{pd.lastEnd}
		)
