mapper.select:
select b.FNAME EQM_BASE_NAME,
from f left join MDM_EQM_BASE b on f.EQM_BASE_ID=b.EQM_BASE_ID where f.EQM_BASE_ID = #{pd.EQM_BASE_ID}
and ( f.FNAME LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FSPECS LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FTEXTURE LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FMANUFACTURER LIKE '%'+ #{pd.KEYWORDS}+'%' )
