mapper.select:
SELECT count(*) zs FROM MDM_REPAIR_REPORTWORK WHERE (
(START_TIME < #{END_TIME} AND START_TIME > #{START_TIME}) 
OR (END_TIME > #{START_TIME} AND END_TIME < #{END_TIME}) 
OR (START_TIME = #{START_TIME} OR END_TIME = #{END_TIME}) 
OR (START_TIME > #{START_TIME} AND END_TIME < #{END_TIME}) 
OR (START_TIME < #{START_TIME} AND END_TIME > #{END_TIME})
) 
AND FSTAFF = #{FSTAFF} 

	AND REPORTWORK_ID <> #{REPORTWORK_ID}

select
from f where f.REPAIR_WORKORDER_ID = #{pd.REPAIR_WORKORDER_ID}
and f.FSTAFF LIKE '%'+ #{pd.KEYWORDS}+'%'
