mapper.select:
select ,e.FNAME EQM_BASE_NAME from f LEFT JOIN MDM_EQM_BASE e ON f.EQM_BASE_ID = e.EQM_BASE_ID where f.REPAIR_WORKORDER_ID = #{pd.REPAIR_WORKORDER_ID}
and ( f.EQM_SUPPLIER LIKE '%'+ #{pd.KEYWORDS}+'%' or f.MAT_CODE LIKE '%'+ #{pd.KEYWORDS}+'%' or f.SPARE_NAME LIKE '%'+ #{pd.KEYWORDS}+'%' or f.SPARE_TYPE LIKE '%'+ #{pd.KEYWORDS}+'%' or f.EQM_DEP LIKE '%'+ #{pd.KEYWORDS}+'%' or f.EQM_STATION LIKE '%'+ #{pd.KEYWORDS}+'%' or e.FNAME LIKE '%'+ #{pd.KEYWORDS}+'%' )
[fhstart] order by f.CREATE_TIME DESC [fhend]
