mapper.update:
update

set 
	RunningState = #{RunningState},
	TransitionType = #{TransitionType}
where 
	MaterialTransferApplicationForm_ID = #{MaterialTransferApplicationForm_ID}

mapper.select:
select isnull(count(*),0) zs from 
 f 
where f.FNum = #{FNum}

	and f.MaterialTransferApplicationForm_ID <> #{MaterialTransferApplicationForm_ID}

select
s.name FCreatePerson,s1.name TransferorPerson,s2.name FReviewerPerson,
c.FName Customer_Name,
from 
 f 
LEFT JOIN OA_STAFF s ON f.FCreator=s.staff_id 
LEFT JOIN OA_STAFF s1 ON f.Transferor=s1.staff_id 
LEFT JOIN OA_STAFF s2 ON f.FReviewer=s2.staff_id 
LEFT JOIN KM_Customer c ON c.Customer_ID=f.FCustomer 
where 1=1

	and
		(
			f.FNum LIKE '%'+ #{pd.KEYWORDS}+'%'
		)


	and f.FNum = #{pd.FNum}


	and f.FPriorityID = #{pd.FPriorityID}


	and f.RunningState = #{pd.RunningState}


	and f.TransferType = #{pd.TransferType}


	and f.FCreator = #{pd.FCreator}


	and f.TransitionType = #{pd.TransitionType}

[fhstart] order by f.PreparationTime desc [fhend]

select
s.name FCreatePerson,s1.name TransferorPerson,s2.name FReviewerPerson,
c.FName Customer_Name,
from 
 f 
LEFT JOIN OA_STAFF s ON f.FCreator=s.staff_id 
LEFT JOIN OA_STAFF s1 ON f.Transferor=s1.staff_id 
LEFT JOIN OA_STAFF s2 ON f.FReviewer=s2.staff_id 
LEFT JOIN KM_Customer c ON c.Customer_ID=f.FCustomer 
where 1=1 

	and
		(
			f.FNum LIKE '%'+ #{KEYWORDS}+'%'
		)


	and f.FNum = #{FNum}


	and f.FPriorityID = #{FPriorityID}


	and f.RunningState = #{RunningState}


	and f.TransferType = #{TransferType}


	and f.FCreator = #{FCreator}


	and f.TransitionType = #{TransitionType}

order by f.PreparationTime desc

select
s.name FCreatePerson,s1.name TransferorPerson,s2.name FReviewerPerson,
c.FName Customer_Name,
from 
 f 
LEFT JOIN OA_STAFF s ON f.FCreator=s.staff_id 
LEFT JOIN OA_STAFF s1 ON f.Transferor=s1.staff_id 
LEFT JOIN OA_STAFF s2 ON f.FReviewer=s2.staff_id 
LEFT JOIN KM_Customer c ON c.Customer_ID=f.FCustomer 
where 1=1

	and
		(
			f.FNum LIKE '%'+ #{pd.KEYWORDS}+'%'
		)


	and f.FNum = #{pd.FNum}


	and f.FPriorityID = #{pd.FPriorityID}


	and f.RunningState = #{pd.RunningState}


	and f.TransferType = #{pd.TransferType}


	and s.name = #{pd.FCreator}


	and f.TransitionType = #{pd.TransitionType}


	and f.PreparationTime >= #{pd.lastStart}


	and f.PreparationTime <= #{pd.lastEnd}
