mapper.select:
select , f2.FNAME FUnitName,f3.NAME FCreatorName, w.FNAME WarehouseName,w.FCODE WarehouseCode, l.FNAME PositionName,l.FCODE PositionCode, case when f.ItemID is null or f.ItemID='' then '' else isnull(f1.MAT_CODE,'无编号')+'/'+isnull(f1.MAT_NAME,'无名称') end FMatJoint, f1.MAT_CODE,f1.MAT_NAME from f left join MBASE_MAT_BASIC f1 on f1.MAT_BASIC_ID=f.ItemID left join MBASE_UNIT_INFO f2 on f1.MAT_MAIN_UNIT=f2.UNIT_INFO_ID left join OA_STAFF f3 on f.FCreator=f3.STAFF_ID left join MWMS_WH_WAREHOUSE w on f.WH_WAREHOUSE_ID=w.WH_WAREHOUSE_ID left join MWMS_WH_LOCATION l on f.WH_LOCATION_ID=l.WH_LOCATION_ID where 1=1
and f.FRelatedID = #{pd.FRelatedID}
and f.FUniqueCode = #{pd.FUniqueCode}
and ( f.FUniqueCode LIKE '%'+ #{pd.KEYWORDS}+'%' )
[fhstart] order by f.FCreatetime desc [fhend]

select w.FNAME WarehouseName,w.FCODE WarehouseCode, l.FNAME PositionName,l.FCODE PositionCode,
from f left join MWMS_WH_WAREHOUSE w on f.WH_WAREHOUSE_ID=w.WH_WAREHOUSE_ID left join MWMS_WH_LOCATION l on f.WH_LOCATION_ID=l.WH_LOCATION_ID where 1=1
and f.FRelatedID = #{FRelatedID}
and f.FUniqueCode = #{FUniqueCode}

SELECT * FROM MM_MaterialTransferSplit where FRelatedID in(
	SELECT MTADetails_ID FROM MM_MaterialTransferApplicationDetails t1 
	LEFT JOIN MBASE_MAT_BASIC t2 on t1.MaterialId=t2.MAT_BASIC_ID
	WHERE t1.TransitionType='转出' 
	AND t2.UNIQUE_CODE_WHETHER='是' 
	AND t1.MTA_ID = #{MTA_ID} 
	AND t1.SPropKey = #{SPropKey} 
	AND MaterialNum = #{MaterialNum}
)
