mapper.select:
select
from f where 1=1
and ( f.FNAME LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FCREATOR LIKE '%'+ #{pd.KEYWORDS}+'%' or f.BELONG_TYPE LIKE '%'+ #{pd.KEYWORDS}+'%'
)
and ( f.BELONG_TYPE = #{pd.BELONGTYPE}
)
and f.TEMPLATE_STATUS = 'YES' and f.FRUN_STATE = '已发布'
[fhstart] order by f.FRUN_STATE, f.CREATE_TIME DESC [fhend]
