mapper.select:
select s.NAME StaffName, from f left join OA_STAFF s on f.FOperatorID=s.STAFF_ID where 1=1
and ( f.FunctionType LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FunctionItem LIKE '%'+ #{pd.KEYWORDS}+'%' or f.OperationType LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FOperatorID LIKE '%'+ #{pd.KEYWORDS}+'%' or s.NAME LIKE '%'+ #{pd.KEYWORDS}+'%' )
and f.FunctionType = #{pd.FunctionType}
and f.FunctionItem = #{pd.FunctionItem}
and f.OperationType = #{pd.OperationType}
and s.NAME = #{pd.StaffName}
[fhstart] ORDER BY f.FOperateTime DESC [fhend]

select s.NAME StaffName, from f left join OA_STAFF s on f.FOperatorID=s.STAFF_ID where 1=1
and ( f.FunctionType LIKE '%'+ #{KEYWORDS}+'%' or f.FunctionItem LIKE '%'+ #{KEYWORDS}+'%' or f.OperationType LIKE '%'+ #{KEYWORDS}+'%' or f.FOperatorID LIKE '%'+ #{KEYWORDS}+'%' or s.NAME LIKE '%'+ #{KEYWORDS}+'%' )
and f.FunctionType = #{FunctionType}
and f.FunctionItem = #{FunctionItem}
and f.OperationType = #{OperationType}
and s.NAME = #{StaffName}
ORDER BY f.FOperateTime DESC
