mapper.select:
select
from f where 1=1 and STORAGE_UNIT_ID=#{pd.STORAGE_UNIT_ID}
and ( SUBLOT_NAME LIKE '%'+ #{pd.KEYWORDS}+'%'
)
[fhstart] order by FTIME DESC [fhend]

SELECT TOP 1  

FROM MPOT_STORAGE_BILL f 
WHERE f.STORAGE_UNIT_ID = #{STORAGE_UNIT_ID} 
	AND f.MAT_CODE=#{MAT_CODE} 
	AND f.STORAGE_AMOUNT=#{STORAGE_AMOUNT} 
	AND INPUT_OUT_TYPE='input' 
	AND SUBLOT_STATE='锁定'
ORDER BY FTIME DESC

SELECT TOP 1  

FROM MPOT_STORAGE_BILL f 
WHERE f.STORAGE_UNIT_ID = #{STORAGE_UNIT_ID} 
	AND f.MAT_CODE=#{MAT_CODE} 
	AND f.STORAGE_AMOUNT=#{STORAGE_AMOUNT} 
	AND INPUT_OUT_TYPE='input' 
	AND SUBLOT_STATE='锁定'
ORDER BY FTIME
