mapper.select:
select
,area.FNAME AS AREANAME,t.FNAME AS TEAMNAME 
from 
 f 
LEFT JOIN MDM_AREA area ON f.AREA_ID = area.AREA_ID 
LEFT JOIN MBASE_TEAM_INFO t ON f.TEAM_ID = t.TEAM_INFO_ID 
where 1=1 

	and
		(
			area.FNAME LIKE '%'+ #{KEYWORDS}+'%'
			 or 
			t.FNAME LIKE '%'+ #{KEYWORDS}+'%' 
		)


	and
		(
			f.START_TIME >= #{lastStart}
		)


	and
		(
			f.START_TIME <= #{lastEnd}
		)


	and
		(
			f.AREA_ID = #{AREA_ID}
		)


	and
		(
			f.TEAM_ID = #{TEAM_ID}
		)

and f.START_TIME >= CONVERT(varchar,GETDATE(),120)

DECLARE
	@starttime datetime ,@endtime datetime
SET @starttime = #{START_TIME}
SET @endtime = #{END_TIME}
SELECT
	count(*) as num
FROM
	MBASE_TEAM_CALENDAR f
WHERE
	1 = 1
AND (
	f.TEAM_ID = #{TEAM_ID}
) 
AND (
	(
		f.START_TIME > @starttime
		AND f.START_TIME < @endtime
	)
	OR (
		f.START_TIME < @starttime
		AND f.END_TIME > @endtime
	)
	OR (
		f.END_TIME > @starttime
		AND f.END_TIME < @endtime
	)
)
