mapper.update:
update

set 
	FINSTRUCTOR_NAME = #{FINSTRUCTOR_NAME},
	FINSTRUCTOR_PATH = #{FINSTRUCTOR_PATH}
where 
	WC_STATION_ID = #{WC_STATION_ID}

mapper.select:
select isnull(count(*),0) zs from 
 f 
where f.FCODE = #{FCODE} 

	and f.WC_STATION_ID <> #{WC_STATION_ID}

    SELECT ISNULL((
	SELECT top 1 ISNULL(f.FINSTRUCTOR_PATH , '') AS FINSTRUCTOR_PATH FROM MDM_WC_STATION f WHERE RIGHT(f.FINSTRUCTOR_PATH,3) ='pdf'

		and
			(
				f.FNAME = #{KEYWORDS}
				 or 
				f.FCODE = #{KEYWORDS}
				or 
				f.WC_STATION_ID = #{KEYWORDS}
			)

	), '无') AS FINSTRUCTOR_PATH
