mapper.select:
select isnull(count(*),0) zs from 
 f 
where f.FCODE = #{FCODE} 

	and f.WH_LOCATION_ID <> #{WH_LOCATION_ID}


	and f.WH_STORAGEAREA_ID = #{WH_STORAGEAREA_ID}

		SELECT TOP 1 f.WH_LOCATION_ID,f.WH_STORAGEAREA_ID,f1.FNAME AS WAREHOUSE_NAME,f1.FCODE AS WAREHOUSE_CODE,f.FNAME AS LOCATION_NAME,f.FCODE AS LOCATION_CODE FROM MWMS_WH_LOCATION f LEFT JOIN MWMS_WH_WAREHOUSE f1
        ON f.WH_STORAGEAREA_ID=f1.WH_WAREHOUSE_ID
        WHERE f1.FCODE=#{warehouseCode} AND f.FCODE=#{locationCode}
