mapper.select:
select isnull(count(*),0) zs from 
 f 
where f.FCODE = #{FCODE} 

	and f.WH_WAREHOUSE_ID <> #{WH_WAREHOUSE_ID}

select
from f where 1=1
and ( f.FNAME LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FCODE LIKE '%'+ #{pd.KEYWORDS}+'%' )
and f.FNAME LIKE '%'+ #{pd.FNAME}+'%'
and f.FCODE LIKE '%'+ #{pd.FCODE}+'%'
and f.PROHIBIT_WHETHER = #{pd.PROHIBIT_WHETHER}
and f.FClass = #{pd.FClass}
and f.FTYPE = #{pd.FTYPE}
[fhstart] ORDER BY f.FCODE [fhend]

select
from f where 1=1
and ( f.FNAME LIKE '%'+ #{KEYWORDS}+'%' or f.FCODE LIKE '%'+ #{KEYWORDS}+'%' )
and f.FNAME LIKE '%'+ #{FNAME}+'%'
and f.FCODE LIKE '%'+ #{FCODE}+'%'
and f.PROHIBIT_WHETHER = #{PROHIBIT_WHETHER}
and f.FClass = #{FClass}
and f.FTYPE = #{FTYPE}
ORDER BY f.FCODE

select 

from 
 f 
where 1=1 

	and f.FNAME LIKE '%'+ #{FNAME}+'%' 


	and f.FCODE LIKE '%'+ #{FCODE}+'%'


	and f.PROHIBIT_WHETHER = #{PROHIBIT_WHETHER}


	and f.FClass = #{FClass}


