mapper.delete:
		delete from

		where 
		1=1


                    and DOUBLE_PLAN_WEEK_MAIN_ID=#{DOUBLE_PLAN_WEEK_MAIN_ID}


                    and DOUBLE_PLAN_WEEK_MAIN_ID=#{DOUBLE_PLAN_WEEK_MAIN_ID}


                	and DOUBLE_PLAN_WEEK_RISK_ID = #{DOUBLE_PLAN_WEEK_RISK_ID}

mapper.update:
update
set FORDER = #{FORDER}, FLEVEL = #{FLEVEL}, FDECRIBE = #{FDECRIBE}, FMEASURE = #{FMEASURE}, FPRINCIPAL = #{FPRINCIPAL}, FTRACE_ICON = #{FTRACE_ICON},
FNISHTIME = #{FNISHTIME},
