mapper.select:
select
from f where 1=1 and f.PHASETEMPLATE_ID=#{pd.PLANINSTANCE_ID}
and ( f.THEME LIKE '%'+ #{pd.KEYWORDS}+'%' or f.LOCATION LIKE '%'+ #{pd.KEYWORDS}+'%' or f.ORIGINATOR LIKE '%'+ #{pd.KEYWORDS}+'%' or f.COMPILINGPERSON LIKE '%'+ #{pd.KEYWORDS}+'%' or f.CHECKER LIKE '%'+ #{pd.KEYWORDS}+'%' )
