mapper.select:
select
from f where 1=1
and (
f.REQUISITIONNUM LIKE '%'+ #{pd.KEYWORDS}+'%'
)
and (
f.EQUIPMENTNAME = #{pd.EQUIPMENTNAME}
)
and (
f.FSTATE = #{pd.FSTATES}
)
[fhstart] ORDER BY f.FCREATETIME DESC [fhend]

SELECT f.REQUISITIONNUM, f.EQUIPMENTNAME, f.FSTATE, f.FREMARKS, f.FRECEIVER, f.FCREATEPERSONID, f.FCREATETIME, f.S_001 EQM_ID, f.S_002 FRECEIVER_TIME, f.S_003 FTYPE, f.S_004 BILLNO, f.S_005 BILLID, f.SPAREREQUISITION_ID, f1.MATERIALNUM, f1.MATERIALNAME, f1.FUNIT, f1.ACTUALQUANTITY, f1.WAREHOUSEID, f1.POSITIONID FROM MDM_EQM_SPAREREQUISITION f RIGHT JOIN MDM_EQM_SPAREREQUISITIONMX f1 ON f.SPAREREQUISITION_ID = f1.SPAREREQUISITION_ID WHERE 1=1 AND f.FSTATE = '已领'
and f.S_005=#{pd.BILLID}
and ( f.REQUISITIONNUM LIKE '%'+ #{pd.KEYWORDS}+'%' or f.EQUIPMENTNAME LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FRECEIVER LIKE '%'+ #{pd.KEYWORDS}+'%' or f.FCREATEPERSONID LIKE '%'+ #{pd.KEYWORDS}+'%' or f.S_003 LIKE '%'+ #{pd.KEYWORDS}+'%' or f.S_004 LIKE '%'+ #{pd.KEYWORDS}+'%' or f1.MATERIALNUM LIKE '%'+ #{pd.KEYWORDS}+'%' or f1.MATERIALNAME LIKE '%'+ #{pd.KEYWORDS}+'%' or f1.WAREHOUSEID LIKE '%'+ #{pd.KEYWORDS}+'%' or f1.POSITIONID LIKE '%'+ #{pd.KEYWORDS}+'%'
)
and ( f.REQUISITIONNUM LIKE '%'+ #{pd.KEYWORDS1}+'%'
)
and ( f.EQUIPMENTNAME LIKE '%'+ #{pd.KEYWORDS2}+'%'
)
and ( f1.MATERIALNUM LIKE '%'+ #{pd.KEYWORDS3}+'%' or f1.MATERIALNAME LIKE '%'+ #{pd.KEYWORDS3}+'%'
)
and ( f1.POSITIONID LIKE '%'+ #{pd.KEYWORDS4}+'%'
)
[fhstart] ORDER BY f.FCREATETIME DESC [fhend]
