mapper.select:
select ,f1.MAT_NAME,f1.MAT_CODE,f1.UnitName,f2.OrderNum,f3.MAT_NAME ORDERMAT_NAME,f4.WorkOrderNum,f5.NAME FPushPeople from f LEFT JOIN (SELECT t.MAT_BASIC_ID,t.MAT_NAME,t.MAT_CODE,t1.FNAME UnitName FROM MBASE_MAT_BASIC t LEFT JOIN MBASE_UNIT_INFO t1 ON t.MAT_AUXILIARY_UNIT=t1.UNIT_INFO_ID) f1 ON f.MaterialID = f1.MAT_BASIC_ID LEFT JOIN PP_SalesOrder f2 ON f.SalesOrder_ID=f2.SalesOrder_ID LEFT JOIN MBASE_MAT_BASIC f3 ON f.SalesOrderMaterial_ID=f3.MAT_BASIC_ID LEFT JOIN PP_PlanningWorkOrder f4 ON f.PlanningWorkOrder_ID=f4.PlanningWorkOrder_ID LEFT JOIN OA_STAFF f5 ON f.FPushPeopleID= f5.STAFF_ID where f.PurchaseApplyFor_ID = #{pd.PurchaseApplyFor_ID}
and ( f1.MAT_NAME LIKE '%'+ #{pd.KEYWORDS}+'%' or f1.MAT_CODE LIKE '%'+ #{pd.KEYWORDS}+'%' or f2.OrderNum LIKE '%'+ #{pd.KEYWORDS}+'%' or f3.MAT_NAME LIKE '%'+ #{pd.KEYWORDS}+'%' or f4.WorkOrderNum LIKE '%'+ #{pd.KEYWORDS}+'%' or f5.NAME LIKE '%'+ #{pd.KEYWORDS}+'%' )
[fhstart] order by WorkOrderNum DESC [fhend]

mapper.update:
update

set 
	PurchaseApplyFor_ID = #{PurchaseApplyFor_ID},
	FIfCreate = #{FIfCreate}
where 
	PurchaseApplyForMx_ID = #{PurchaseApplyForMx_ID}
