mapper.select:
select f.PurchaseApply_ID, f.FCustomer, f2.FName FCustName, f.RelationNum, f.BusinessType, f.DataSources, f.FNum, f.FOrderer, f.FExplanation FExplanationMain, f.ApplyPerson, f.FMakeBillsPersoID, f.FMakeBillsTime, f.FCheckPersonID, f.FCheckTime, f.FCheckFlag, isnull(f2.FName,'无名称') +'/'+isnull(f2.FNum,'无编号') FCustomerJoint, f3.NAME FOrdererName, f4.NAME ApplyPersonName, f5.NAME FMakeBillsPersoIDName, f6.NAME FCheckPersonIDName,
f8.FNAME FUnitName, f7.MAT_CODE,f7.MAT_NAME, isnull(f7.MAT_CODE,'无编号')+'/'+isnull(f7.MAT_NAME,'无名称') FMatJoint, f7.MAT_AUXILIARY_ID,
f11.OrderNum OrderNum, f12.WorkOrderNum WorkOrderNum, f14.NAME DENAME from PP_PurchaseApply f
left join KM_Customer f2 on f.FCustomer=f2.Customer_ID left join OA_STAFF f3 on f.FOrderer=f3.STAFF_ID left join OA_STAFF f4 on f.ApplyPerson=f4.STAFF_ID left join OA_STAFF f5 on f.FMakeBillsPersoID=f5.STAFF_ID left join OA_STAFF f6 on f.FCheckPersonID=f6.STAFF_ID left join MBASE_MAT_BASIC f7 on f7.MAT_BASIC_ID=f.MaterialID left join MBASE_UNIT_INFO f8 on f7.MAT_MAIN_UNIT=f8.UNIT_INFO_ID
left join PP_SalesOrder f11 on f.SalesOrder_ID = f11.SalesOrder_ID left join PP_PlanningWorkOrder f12 on f.PlanningWorkOrder_ID = f12.PlanningWorkOrder_ID left join SYS_DICTIONARIES f14 on f7.MAT_ATTRIBUTE = f14.DICTIONARIES_ID where 1=1
and f.FNum LIKE '%'+ #{pd.KEYWORDS_FNum}+'%' or f11.OrderNum LIKE '%'+ #{pd.KEYWORDS_FNum}+'%'
and f7.MAT_CODE LIKE '%'+ #{pd.KEYWORDS_MaterialNum}+'%' or f14.NAME LIKE '%'+ #{pd.KEYWORDS_FNum}+'%'
and f7.MAT_NAME LIKE '%'+ #{pd.KEYWORDS_MaterialName}+'%'
and convert(varchar(10), f.FMakeBillsTime) >= #{pd.KEYWORDS_FMakeBillsTimeStart}
and convert(varchar(10), f.FMakeBillsTime) <= #{pd.KEYWORDS_FMakeBillsTimeEnd}
and convert(varchar(10), f1.DemandTime) >= #{pd.KEYWORDS_DemandTimeStart}
and convert(varchar(10), f1.DemandTime) <= #{pd.KEYWORDS_DemandTimeEnd}
[fhstart] order by FMakeBillsTime desc [fhend]

select top 100 FNum from f where 1=1
and ( FNum LIKE '%'+ #{KEYWORDS}+'%'
)
and f.FCustomer = #{FCustomer}
order by f.FNum
