mapper.update:
update

set 
	EndTime = #{EndTime}
where 
	WorkingProcedureExampleID = #{ProcessWorkOrderExample_ID}
	and (EndTime is null or EndTime ='')

mapper.select:
SELECT MIN(f.BeginTime) BeginTime,MAX(f.EndTime) EndTime,Convert(decimal(18,2),Convert(decimal(18,2),SUM(DATEDIFF( Minute, f.BeginTime, f.EndTime)))/60) NUM 
FROM PP_O_RECORD f
WHERE f.WorkingProcedureExampleID=#{ProcessWorkOrderExample_ID} AND f.OPerson=#{NAME}
