mapper.select:
select , f1.Cabinet_Assembly_ID, f1.Cabinet_No, f1.Cabinet_Aliases, f1.Responsible_Technology, f1.If_Bom_Done, f1.If_Drawings_Done, f1.FStatus, f1.Distribute_Time, f1.PRO_PLAN_ID, f1.PROJECT_ID, f1.Load_Status, f1.Bom_Done_Time, f1.Drawings_Done_Time, f1.BatchNum, f1.If_Sync_Done, f1.If_Purchase_Done, f1.Load_Dispach_Time, f1.FAudit_State FAudit_State1, f1.FAudit_CabinetDJ_State, f1.FAudit_Cabinet_State, project.PPROJECT_CODE, project.PPROJECT_NAME, staff.NAME as Responsible_Technology_Name from f inner join PMS_Cabinet_Assembly_Detail f1 on f.CABINET_ASSEMBLY_DETAIL_ID=f1.CABINET_ASSEMBLY_DETAIL_ID left join oa_staff staff on staff.STAFF_ID = f1.Responsible_Technology left join DT_PROJECT project on project.PROJECT_ID = f1.PROJECT_ID where 1=1 and (FIs_Need_Audit ='是' or FIs_NeedDJ_Audit ='是') and (staff.NAME =#{pd.Responsible_Technology_Name} or project.PPROJECT_MANAGER =#{pd.Responsible_Technology_Name})
and ( project.PPROJECT_CODE LIKE '%'+ #{pd.KEYWORDS}+'%' OR project.PPROJECT_NAME LIKE '%'+ #{pd.KEYWORDS}+'%' )
and f1.Cabinet_Type = #{pd.Cabinet_Type}
and f.FTYPE = #{pd.BTStatus}
and (f.FAUDIT_STATE = #{pd.FAudit_State}) [fhstart] order by FCTIME desc [fhend]

select
from f where 1=1 and CABINET_ASSEMBLY_DETAIL_ID=#{pd.CABINET_ASSEMBLY_DETAIL_ID}
and ( FAUDIT_FNOTE LIKE '%'+ #{pd.KEYWORDS}+'%' or FAUDIT_STATE LIKE '%'+ #{pd.KEYWORDS}+'%' )
and f.FTYPE = #{pd.BTStatus}
[fhstart] order by FCTIME desc [fhend]
