mapper.select:
select project.PDELIVERY_DATE, (case when (f.Cabinet_Type is null or f.Cabinet_Type ='') then 1 else 0 end) isEdit, (case when (f.Cabinet_Type is null or f.Cabinet_Type ='') then 'add' else 'edit' end) FFTYPE, ,staff.Name as Technical_Officer_Name, isnull((select count(*) from PMS_Cabinet_Assembly_Detail where Cabinet_Assembly_ID=f.Cabinet_Assembly_ID and FStatus='创建') ,0) FNUM_WXF, isnull((select count(*) from PMS_Cabinet_Assembly_Detail where Cabinet_Assembly_ID=f.Cabinet_Assembly_ID and FStatus!='创建') ,0) FNUM_YXF from f left join oa_staff staff on staff.STAFF_ID = f.Technical_Officer left join DT_PROJECT project on project.PROJECT_ID = f.PROJECT_ID where 1=1
and (
)
and PRO_PLAN_ID = #{pd.PRO_PLAN_ID}
and PROJECT_ID = #{pd.PROJECT_ID}
[fhstart] order by FCTIME desc [fhend]

select
from f where 1=1
