mapper.select:
select

from 
 f
where 1=1

and f.Cabinet_Assembly_Detail_ID = #{pd.Cabinet_Assembly_Detail_ID}


and f.MAT_CODE = #{pd.MAT_CODE}


and f.FSTATE = #{pd.FSTATE}


	and
		(

		)

select
from f where 1=1
and f.Cabinet_Assembly_Detail_ID = #{Cabinet_Assembly_Detail_ID}
and f.MAT_BASIC_ID = #{MAT_BASIC_ID}
and f.FSTATE = #{FSTATE}
and MAT_BASIC_ID !='' and MAT_BASIC_ID is not null and MAT_CODE !='' and MAT_CODE is not null and BOM_COUNT is not null
order by f.FSTATE,cast(isnull(FORDER,0) as int)

SELECT SUM (CONVERT(DECIMAL(10,2),bom.BOM_COUNT)) as BOM_COUNT, bom.MAT_BASIC_ID, bom.MAT_CODE, bom.MAT_NAME, bom.MAT_SPECS, bom.MAT_MAIN_UNIT, f2.Cabinet_No, SUM (CONVERT(DECIMAL(10,2),bom.BOM_COUNT))-sum(cast(isnull(f3.FSUM,'0') as NUMERIC(24,2))) CY_COUNT FROM PMS_Cabinet_BOM bom left join PMS_Cabinet_Assembly_Detail f2 on bom.Cabinet_Assembly_Detail_ID=f2.Cabinet_Assembly_Detail_ID left join ( select Material_ID,sum(QuantityCount) FSUM from MM_CallMaterialFL f1 left join MM_CallMaterialDetailsFL f2 on f1.CallMaterialFL_ID=f2.CallMaterialFL_ID where FState='车间接收' and f1.PlanningWorkOrderNum=(select Cabinet_No from PMS_Cabinet_Assembly_Detail where 1=1
and Cabinet_Assembly_Detail_ID = #{Cabinet_Assembly_Detail_ID}
and 1=2
) group by Material_ID ) f3 on bom.MAT_BASIC_ID=f3.Material_ID where 1=1
and bom.Cabinet_Assembly_Detail_ID = #{Cabinet_Assembly_Detail_ID}
and bom.MAT_CODE = #{MAT_CODE}
and bom.MAT_BASIC_ID = #{materialId}
group by f2.Cabinet_No, bom.MAT_BASIC_ID, bom.MAT_CODE, bom.MAT_NAME, bom.MAT_SPECS, bom.MAT_MAIN_UNIT

select
count(*) FNUM
from 
 f 
where Cabinet_BOM_ID !=#{Cabinet_BOM_ID}
and Cabinet_Assembly_Detail_ID=#{Cabinet_Assembly_Detail_ID}
and MAT_BASIC_ID=#{MAT_BASIC_ID}
