mapper.delete:
		delete from

		where 
		1=1


                    and GE_MUTILIBPHNEW_ID=#{GE_MUTILIBPHNEW_ID}


                   and
				   RUN_PH_ID =#{RUN_PH_ID}


                   and
				   RUN_O_ID =#{RUN_O_ID}


                   and
				   RUN_UP_ID =#{RUN_UP_ID}


				and RUN_P_ID=#{RUN_P_ID}


                and RUN_FINAL_INSPECT_ID = #{RUN_FINAL_INSPECT_ID}

mapper.select:
		select 

		from 
		 f
		where 1=1


                    and f.GE_MUTILIBPHNEW_ID=#{GE_MUTILIBPHNEW_ID}



                	and f.RUN_FINAL_INSPECT_ID = #{RUN_FINAL_INSPECT_ID}
