mapper.select:
select top 10
,g.FNAME as LOCAL_NAME,g.FACTORY_AREA
from 
 f
left join HSE_TROUBLELOCAL g on f.LOCAL_ID = g.TROUBLELOCAL_ID
where 1=1

	and
		(
		f.STATUS = #{STATUS} and f.IMG_URL is not null
		)


	and
	(
	f.RESULT_STATUS = #{RESULT_STATUS}
	)


	and
	(
	f.CREATE_TIME >= #{dateStart} and f.CREATE_TIME <= #{dateEnd}
	)


	and
	(
	f.IS_ERROR = #{IS_ERROR}
	)


	and
	(
	f.RESULT_STATUS = #{RESULT_STATUS}
	)


	and
	(
	f.CREATE_TIME <= DATEADD(month, -3, GETDATE())
	)

order by f.CREATE_TIME
