mapper.update:
update
set
QUESTION_TITLE = #{QUESTION_TITLE}, SCOPE_OF_INFLUENCE = #{SCOPE_OF_INFLUENCE}, PROBLEM_DESCRIPTION = #{PROBLEM_DESCRIPTION}, PROBLEM_SOURCE = #{PROBLEM_SOURCE}, RESPONSIBILITY_JUDGMENT = #{RESPONSIBILITY_JUDGMENT}, SEVERITY = #{SEVERITY}, PERSON_LIABLE = #{PERSON_LIABLE}
where QUESTIONBANK_ID = #{QUESTIONBANK_ID}

mapper.select:
select

from 
 f
where 1=1 and PROPOSER=#{pd.Loginperson}

	and
		(

	QUESTION_TITLE LIKE '%'+ #{pd.KEYWORDS}+'%'
			 or
	PROBLEM_DESCRIPTION LIKE '%'+ #{pd.KEYWORDS}+'%'

		)


	and
	(

	SCOPE_OF_INFLUENCE=#{pd.SCOPE_OF_INFLUENCE}


	)


	and
	(

	PROBLEM_SOURCE=#{pd.PROBLEM_SOURCE}


	)


	and
	(

	RESPONSIBILITY_JUDGMENT=#{pd.RESPONSIBILITY_JUDGMENT}


	)


	and
	(

	SEVERITY=#{pd.SEVERITY}


	)


	and
	(

	STATE=#{pd.STATE}


	)

[fhstart] order by f.QUESTION_NUMBER  asc [fhend]
