mapper.update:
update
set QUESTION_NUMBER = #{QUESTION_NUMBER}, QUESTION_TITLE = #{QUESTION_TITLE}, SCOPE_OF_INFLUENCE = #{SCOPE_OF_INFLUENCE}, PROBLEM_DESCRIPTION = #{PROBLEM_DESCRIPTION}, PROBLEM_SOURCE = #{PROBLEM_SOURCE}, RESPONSIBILITY_JUDGMENT = #{RESPONSIBILITY_JUDGMENT}, SEVERITY = #{SEVERITY}, PERSON_LIABLE = #{PERSON_LIABLE}, PROPOSER = #{PROPOSER},
STATE = #{STATE}, QUESTIONBANK_ID = QUESTIONBANK_ID where QUESTIONBANK_ID = #{QUESTIONBANK_ID}

mapper.select:
select

from
 f
where  PERSON_LIABLE=#{pd.login}

	and
	(
	f.SCOPE_OF_INFLUENCE = #{pd.SCOPE_OF_INFLUENCE}
	)
